<?php// * * * * * * * *Get all the vars * * * * * * *$name = filter_input(INPUT_POST, 'companyName', FILTER_SANITIZE_SPECIAL_CHARS); $contact = filter_input(INPUT_POST, 'contact', FILTER_SANITIZE_SPECIAL_CHARS);$phone = filter_input(INPUT_POST, 'phone', FILTER_SANITIZE_SPECIAL_CHARS);$email_from = filter_input(INPUT_POST, 'email', FILTER_SANITIZE_SPECIAL_CHARS);$invNo = $_POST['invNo'];$queryType = $_POST['typeOfQueryDropDownBox'];$details = $_POST['details'];$generalDetails = filter_input(INPUT_POST, 'generalDetails', FILTER_SANITIZE_SPECIAL_CHARS);$hireRatesCB = filter_input(INPUT_POST, 'hireRatesCB', FILTER_SANITIZE_SPECIAL_CHARS);$salesmanVistCB = filter_input(INPUT_POST, 'salesmanVistCB', FILTER_SANITIZE_SPECIAL_CHARS);
$headers = "MIME-Version: 1.0\r\n";
$headers .= "Content-Type: text/html; charset=ISO-8859-1\r\n";

// Begin to create the email message. note \n is a break line in outlook. 	$email_message = '<html><body>';	$email_message .= '<h1>Hello, World!</h1>';	$email_message .= "---------- Customer Details ----------\n";    $email_message .= "Company Name:         ".($name)."\n";    $email_message .= "Contact:                          ".($contact)."\n";    $email_message .= "Phone:                            ".($phone)."\n";    $email_message .= "Email:                               ".($email_from)."\n\n";        //Because we can have multiple enquires, we need loop for the amount in the array.        for($i = 0, $l = count($invNo); $i < $l; $i++){  $email_message .= "---------- Invoice Query " . ($i+1) . " ----------" . "\n";  $email_message .= "Invoice No/Contract No:   " . ($invNo[$i]) . "\n";  $email_message .= "Type of Query:                      " . ($queryType[$i]) . "\n";  $email_message .= "Details: \n" . ($details[$i]) . "\n\n\n";}               $email_message .= "---------- General Query ----------"."\n";    $email_message .= "Details: \n".($generalDetails)."\n\n";    $email_message .= "---------- The Customer Needs... ----------"."\n";    $email_message .= "Request hire rates:      ".($hireRatesCB)."\n";       $email_message .= "Supply Catalogues:                ".($salesmanVistCB)."\n";    // Close all stuff
    $email_message .= '</body></html>';        // Change this variable to change the destination of the e-mail.    $email_to = "chris.lannister@thehireman.co.uk";    $email_subject = "QUERY form from - " . $name . ", has been filled in.";    // Mail Push'Reply-To: '.$email_from."\r\n" .'X-Mailer: PHP/' . phpversion();mail($email_to, $email_subject, $email_message, $headers);  ?><!DOCTYPE html><html lang="en"><head>  <meta charset="utf-8">  <meta http-equiv="X-UA-Compatible" content="IE=edge,chrome=1">  <title>Hireman Query Form</title>  <meta name="description" content="Query Form, Typically Account Invoice Requests">  <meta name="author" content="Chris Lannister">  <meta name="viewport" content="width=device-width; initial-scale=1.0">  <!-- Scripts -->  <script src="//ajax.googleapis.com/ajax/libs/jquery/1.8.3/jquery.min.js"></script>  <script src="script.js"></script>  <!-- Stylesheets -->  <link rel="stylesheet" type="text/css" href="css/styles.css"> </head><body><div class="wrapper"> <!-- Confirmation message --> Thank you for contacting us. We will be in touch with you very soon.<br><br>  <img src="" style="margin:30px 0 0 100px; "></div> <!-- End of wrapper --></body></html>